    def check(self):
        results = {
            "status": True,
            "aiWords": None,
            "fakePercentage": None, 
            "isHuman": None,
            "otherFeedback": None, 
            "sentences": None,  
            "textWords": None
        }
        
        try:
            resp = requests.post(self.url, headers=self.headers, data=self.data, verify=False).json()
            
            if resp.get("success"):
                feedback = resp["data"].get("additional_feedback", None) if not resp["data"].get("additional_feedback", None) in [None, ''] else None
                results.update({
                    "isHuman": resp["data"]["isHuman"],
                    "sentences": resp["data"]["h"],
                    "textWords": resp["data"]["textWords"],
                    "aiWords": resp["data"]["aiWords"],
                    "fakePercentage": resp['data']["fakePercentage"],
                    "otherFeedback": feedback
                })
            else:
                results.update({
                    "status": False,
                    "otherFeedback": "Error: Something went wrong."
                })
                
        except Exception as e:
            print(e)
            results.update({
                "status": False,
                "otherFeedback": f"Error: Something went wrong: {str(e)}".replace("api.zerogpt.com", "*********")
            })
        
        return results